#!/usr/bin/env pythonfrom mpi4py import MPIimport numpy as npdef main():    comm = MPI.COMM_WORLD    rank = comm.Get_rank()    if rank == 0:        data = np.arange(100, dtype=np.float64)        comm.Send(data, dest=1, tag=13)    elif rank == 1:        data = np.empty(100, dtype=np.float64)        comm.Recv(data, source=0, tag=13)    print(data)if __name__ == '__main__':    main()